import axios from 'axios';
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import Cloudy from '../assets/cloudy.png';


const DayCard = styled.div`
  height: calc(100% - 20px);
  height: 8vw;
  max-height: 160px;
  min-height: 140px;
  min-width: 180px;
  position: relative;
  transition: transform 250ms;
  width: calc(100% / 7);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  margin: 10px 10px;
  border-radius: 10px;
  padding: 10px;

  backdrop-filter: blur(3px);
  background-color: rgba(255,255,255, 0.4);
  border: 1px solid rgba(255,255,255, 0.2);
  border-radius: 10px;
  box-shadow: 2px 2px 4px rgba(0, 0,0, 0.25);
  &:last-of-type {
    margin-right: 5px;
  }
`

const Icon = styled.img`
  width: 60px;
  object-fit: fill;
  object-position: center;

  font-size: 3.5em;
  text-shadow: 2px 2px 2px rgba(black, 0.1);
`
const Temperature = styled.div`
  align-items: flex-start;
  display: flex;
  font-size: 0.9em;
  justify-content: center;
  color:#46aab3;
`

const DayName = styled.div`
  font-size: 0.9em;
  text-transform: uppercase;
  color:#46aab3;
`


  const Unit = styled.span`
    font-size: 0.8em;
    margin-left: 3px;
  `

export default function Day() {

  const [responseData, setResponseData] = useState([]);


  let currentDate = new Date()
  let tomorrow;
  let days = [];
  let result;

  const getDates = () => {
    for(let i = 0; i<=3; i++){
      tomorrow = currentDate.setDate(currentDate.getDate() + 1)
      result = new Date(tomorrow).toISOString().replace(/T.*/,'').split('-').reverse().join('-');
      days.push(result);
      currentDate = new Date(tomorrow);
    }
  }

  function getData () {
    for(let i = 0; i<=days.length; i++){
      axios.get(`https://api.weatherapi.com/v1/future.json?key=730a9b93e6c64a57bb9145731222206&q=London&dt=2022-08-10}`).then(function (response) {
        setResponseData(responseData.push(response.data));
      }).catch(function (error) {
        console.error(error);
      });
    }
    console.log(responseData)
  }

  let status;
  const setStatus = () => {
     if(document.readyState === 'interactive'){
      status = 'interactive'
    } else return;
  }
  setStatus();
  let executed = false;
  useEffect(() => {
    getDates()
    if(!executed){
      getData()
      executed = true;
    }
  }, [status])

  return (
    <DayCard>
        <DayName>MON</DayName>
        <Icon src={Cloudy}/>
        <Temperature>20 <Unit>C</Unit></Temperature>
    </DayCard>
  )
}
