import React from 'react';
import styled from 'styled-components';
import Cloudy from '../assets/cloudy.png';

const Container = styled.div`
    width: 100vw;
    height: 400px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    position: relative;
`

const Time = styled.span`
    font-size: 30px;
    font-weight: 700;
    color: white;
    position: absolute;
    top: 20px;
    left: 20px;
`

const CitiesContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  padding: 20px 10px;
  background-color: #2b2b2b;
`

const City = styled.div`
  width: 100px;
  height: 200px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  margin: 10px 10px;
  border-radius: 10px;
  background-color: black;
`

const Icon = styled.img`
  width: 60px;
  object-fit: fill;
  object-position: center;
`

export default function Main() {
  return (
    <Container>
        <Time>11 : 52</Time>
        <CitiesContainer>
          <City>
            <Icon src={Cloudy}/>
            <Name>Nairobi</Name>
            <Temperature>20 C</Temperature>
          </City>
        </CitiesContainer>
    </Container>
  )
}
