import { useEffect, useState } from 'react';
import {LocationOn} from '@mui/icons-material';
import React from 'react';
import styled from 'styled-components';
import Cloudy from '../assets/cloudy.png';

import axios from 'axios';

const Container = styled.div`
    width: 100vw;
    min-height: 350px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-evenly;
    margin: 20px 0px;
    position: relative;
`

const Name = styled.h1`
    font-size: 28px;
    font-weight: 600;
    color: white;
`

const DateContainer = styled.div`
    font-size: 22px;
    font-weight: 500;
    color: #46aab3;
`
const Icon = styled.img`
  width: 100px;
  object-fit: fill;
  object-position: center;

`
const Temperature = styled.div`
  align-items: flex-start;
  display: flex;
  font-size: 22px;
  justify-content: center;
  color:#fff;
`

const Condition = styled.div`
    font-size: 18px;
    color: white;
`

const ButtonContainer = styled.div`
    height: 200px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    position: absolute;
    right: 50px;
`

const Button = styled.button`
    width: 50px;
    height: 50px;
    border-radius: 50%;
    border: 1px solid white;
    background-color: transparent;
    cursor: pointer;
    color: white;
    *{
        font-size: 18px;
        color: white;
    }
`
export default function MainCard({item}) {

    const [responseData, setResponseData] = useState('');
    const [units, setUnits] = useState('C')
    let status;
    const setStatus = () => {
       if(document.readyState === 'interactive'){
        status = 'interactive'

      } else return;
    }
    setStatus();

    const handleUnits = () => {
        if(units === 'C'){
            setUnits('F')
        } else setUnits('C')
    }

    useEffect(() => {
      ( function () {
        const options = {
          method: 'GET',
          url: 'https://community-open-weather-map.p.rapidapi.com/weather',
          params: {
            q: 'Nairobi,ke',
            lat: '0',
            lon: '0',
            callback: 'test',
            id: '2172797',
            lang: 'null',
            units: 'imperial',
            mode: 'xml'
          },
          headers: {
            'X-RapidAPI-Key': '7015901614mshbeaa572245b6bffp123fd4jsn9b3a74df405d',
            'X-RapidAPI-Host': 'community-open-weather-map.p.rapidapi.com'
          }
        };

        axios.get('https://api.weatherapi.com/v1/current.json?key=730a9b93e6c64a57bb9145731222206&q=London&aqi=no').then(function (response) {

          setResponseData(response.data);
          console.log(responseData);
          item(responseData.location.localtime.split(" ")[1])
        }).catch(function (error) {
          console.error(error);
        });
      })()
    }, [status])
  return (
    <Container>
        <Name>
            {responseData && (responseData.location.name  + ", ")}
            {responseData && (responseData.location.country)}
            {!responseData && "Nairobi, Kenya"}
        </Name>
        <DateContainer>{responseData && (responseData.location.localtime.split(" ")[0])}</DateContainer>
        <Icon src={
              !responseData ? Cloudy : "https:" + responseData.current.condition.icon
            }/>
        <Temperature>
        {responseData && (units=== 'C' ? responseData.current.temp_c : responseData.current.temp_f)}°{units}
        </Temperature>
        <Condition>
            {responseData && (responseData.current.condition.text)}
            {!responseData && 'rainy'}
        </Condition>
        <ButtonContainer>
            <Button><LocationOn /></Button>
            <Button onClick={handleUnits}>°{units}</Button>
        </ButtonContainer>
    </Container>
  )
}
