import React from 'react';
import styled from 'styled-components';
import {Clear, RocketLaunch} from '@mui/icons-material';

const Container = styled.div`
  width: 100vw;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  background-color: ${props => props.bg};
  position: fixed;
  top: 0;
`

export default function Notification() {
  return (
    <Container bg='green'>
        Click on the <RocketLaunch /> button to share your current location.
        <Clear styled={{
            color: 'white',
            fontSize: '24px',
            fontWeight: '600',
            position: 'absolute',
            left: '20px',
            cursor: 'pointer'
        }}/>
    </Container>
  )
}
