import React from 'react';
import styled from 'styled-components';
import {Clear, LocationOn} from '@mui/icons-material';

const Container = styled.div`
  width: 100vw;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  background-color: ${props => props.bg};
  position: relative;

  .clear{
    color: 'white';
    font-size: '24px';
    font-weight: '600';
    position: 'absolute';
    right: '20px';
    cursor: 'pointer'
  }
`

export default function Notification() {
  return (
    <Container bg='#00B140'>
        Click on the <LocationOn /> button to share your current location.
        <Clear className='clear'/>
    </Container>
  )
}
